import React, { useEffect, useMemo, useRef, useState } from 'react';
import Colors from '../../constants/colors';

export interface ToolbarProps {
  windows: DesktopWindows;
  toggleMinimize: (key: string) => void;
  shutdown: () => void;
}

type MenuKey = 'apple' | 'app' | 'file' | 'edit' | 'view' | 'go' | 'window' | 'help';

type MenuItem = {
  label: string;
  shortcut?: string; // e.g. "âŒ˜Q"
  disabled?: boolean;
  divider?: boolean;
  onClick?: () => void;
};

const Toolbar: React.FC<ToolbarProps> = ({ windows, toggleMinimize, shutdown }) => {
  const getTime = () => {
    const date = new Date();
    let hours = date.getHours();
    let minutes = date.getMinutes();
    const amPm = hours >= 12 ? 'PM' : 'AM';
    hours = hours % 12;
    hours = hours ? hours : 12;
    const mins = minutes < 10 ? '0' + minutes : minutes;
    return `${hours}:${mins} ${amPm}`;
  };

  const barRef = useRef<HTMLDivElement | null>(null);

  const [time, setTime] = useState(getTime());

  // which menu is open (null => none)
  const [openMenu, setOpenMenu] = useState<MenuKey | null>(null);

  // "Finder" should become active window app name
  const activeAppName = useMemo(() => {
    let max = -Infinity;
    let activeKey = '';
    Object.keys(windows).forEach((k) => {
      if (windows[k]?.zIndex >= max) {
        max = windows[k].zIndex;
        activeKey = k;
      }
    });

    const appName = windows[activeKey]?.name;
    return appName || 'Finder';
  }, [windows]);

  // update clock
  useEffect(() => {
    const t = setInterval(() => setTime(getTime()), 5000);
    return () => clearInterval(t);
  }, []);

  // click outside closes menu
  useEffect(() => {
    const onDown = (e: MouseEvent) => {
      const target = e.target as HTMLElement;
      if (!barRef.current) return;
      if (!barRef.current.contains(target)) {
        setOpenMenu(null);
      }
    };
    window.addEventListener('mousedown', onDown, false);
    return () => window.removeEventListener('mousedown', onDown, false);
  }, []);

  // menu definitions
  const menus: Record<MenuKey, { title: string; items: MenuItem[] }> = useMemo(() => {
    const windowItems: MenuItem[] = [
      { label: 'Minimize', shortcut: 'âŒ˜M', disabled: Object.keys(windows).length === 0, onClick: () => {
          // minimize the top-most window
          let max = -Infinity; let activeKey = '';
          Object.keys(windows).forEach((k) => {
            if (windows[k]?.zIndex >= max) { max = windows[k].zIndex; activeKey = k; }
          });
          if (activeKey) toggleMinimize(activeKey);
          setOpenMenu(null);
        } },
      { label: 'Zoom', shortcut: 'âŒƒâŒ˜Z', disabled: true }, // keep disabled unless you wire it
      { divider: true, label: 'â€”' },
      { label: 'Bring All to Front', disabled: true },
    ];

    return {
      apple: {
        title: 'ï£¿',
        items: [
          { label: 'About SovraTechOS', onClick: () => setOpenMenu(null) },
          { divider: true, label: 'â€”' },
          { label: 'System Preferencesâ€¦', shortcut: 'âŒ˜,', disabled: true },
          { label: 'Dock', disabled: true },
          { divider: true, label: 'â€”' },
          { label: 'Sleep', disabled: true },
          { label: 'Restartâ€¦', disabled: true },
          { label: 'Shut Downâ€¦', shortcut: 'âŒ¥âŒ˜â', onClick: () => { setOpenMenu(null); shutdown(); } },
          { label: 'Log Outâ€¦', disabled: true },
        ],
      },

      app: {
        title: activeAppName,
        items: [
          { label: `About ${activeAppName}`, disabled: true },
          { divider: true, label: 'â€”' },
          { label: 'Preferencesâ€¦', shortcut: 'âŒ˜,', disabled: true },
          { divider: true, label: 'â€”' },
          { label: `Hide ${activeAppName}`, shortcut: 'âŒ˜H', disabled: true },
          { label: 'Hide Others', shortcut: 'âŒ¥âŒ˜H', disabled: true },
          { label: 'Show All', disabled: true },
          { divider: true, label: 'â€”' },
          { label: `Quit ${activeAppName}`, shortcut: 'âŒ˜Q', disabled: true },
        ],
      },

      file: {
        title: 'File',
        items: [
          { label: 'New Finder Window', shortcut: 'âŒ˜N', disabled: true },
          { label: 'New Folder', shortcut: 'â‡§âŒ˜N', disabled: true },
          { divider: true, label: 'â€”' },
          { label: 'Close Window', shortcut: 'âŒ˜W', disabled: true },
        ],
      },

      edit: {
        title: 'Edit',
        items: [
          { label: 'Undo', shortcut: 'âŒ˜Z', disabled: true },
          { label: 'Redo', shortcut: 'â‡§âŒ˜Z', disabled: true },
          { divider: true, label: 'â€”' },
          { label: 'Cut', shortcut: 'âŒ˜X', disabled: true },
          { label: 'Copy', shortcut: 'âŒ˜C', disabled: true },
          { label: 'Paste', shortcut: 'âŒ˜V', disabled: true },
          { label: 'Select All', shortcut: 'âŒ˜A', disabled: true },
        ],
      },

      view: {
        title: 'View',
        items: [
          { label: 'Show Toolbar', disabled: true },
          { label: 'Show Status Bar', disabled: true },
          { divider: true, label: 'â€”' },
          { label: 'Zoom In', shortcut: 'âŒ˜+', disabled: true },
          { label: 'Zoom Out', shortcut: 'âŒ˜âˆ’', disabled: true },
        ],
      },

      go: {
        title: 'Go',
        items: [
          { label: 'Back', shortcut: 'âŒ˜[', disabled: true },
          { label: 'Forward', shortcut: 'âŒ˜]', disabled: true },
          { divider: true, label: 'â€”' },
          { label: 'Applications', shortcut: 'â‡§âŒ˜A', disabled: true },
          { label: 'Desktop', shortcut: 'â‡§âŒ˜D', disabled: true },
          { label: 'Downloads', shortcut: 'âŒ¥âŒ˜L', disabled: true },
        ],
      },

      window: {
        title: 'Window',
        items: windowItems,
      },

      help: {
        title: 'Help',
        items: [
          { label: 'SovraTechOS Help', disabled: true },
          { divider: true, label: 'â€”' },
          { label: 'Search', disabled: true },
        ],
      },
    };
  }, [activeAppName, shutdown, toggleMinimize, windows]);

  const openOrToggle = (key: MenuKey) => {
    setOpenMenu((prev) => (prev === key ? null : key));
  };

  const onHoverMenu = (key: MenuKey) => {
    // mac-like: if a menu is already open, hovering switches menus
    setOpenMenu((prev) => (prev ? key : prev));
  };

  const MenuDropdown = ({ menuKey }: { menuKey: MenuKey }) => {
    const menu = menus[menuKey];
    if (!menu) return null;

    return (
      <div style={styles.dropdown}>
        {menu.items.map((item, idx) => {
          if (item.divider) {
            return <div key={`div-${idx}`} style={styles.divider} />;
          }

          const disabled = !!item.disabled;
          return (
            <div
              key={`${menuKey}-${idx}`}
              style={Object.assign(
                {},
                styles.dropdownItem,
                disabled && styles.dropdownItemDisabled
              )}
              onMouseDown={(e) => {
                e.preventDefault();
                e.stopPropagation();
                if (disabled) return;
                item.onClick && item.onClick();
                // if no click handler, still close menu for realism
                if (!item.onClick) setOpenMenu(null);
              }}
            >
              <span style={styles.itemLabel}>{item.label}</span>
              <span style={styles.itemShortcut}>{item.shortcut || ''}</span>
            </div>
          );
        })}
      </div>
    );
  };

  const MenuButton = ({ k }: { k: MenuKey }) => {
    const title = menus[k].title;

    return (
      <div
        style={Object.assign({}, styles.menuButton, openMenu === k && styles.menuButtonActive)}
        onMouseDown={(e) => {
          e.preventDefault();
          e.stopPropagation();
          openOrToggle(k);
        }}
        onMouseEnter={() => onHoverMenu(k)}
      >
        <p style={Object.assign({}, styles.menuText, k === 'app' && styles.menuBold)}>
          {title}
        </p>

        {openMenu === k && (
          <div style={styles.dropdownWrap}>
            <MenuDropdown menuKey={k} />
          </div>
        )}
      </div>
    );
  };

  return (
    <div ref={barRef} style={styles.toolbarOuter}>
      <div style={styles.menuBarInner}>
        <div style={styles.menuLeft}>
          <MenuButton k="apple" />
          <MenuButton k="app" />
          <MenuButton k="file" />
          <MenuButton k="edit" />
          <MenuButton k="view" />
          <MenuButton k="go" />
          <MenuButton k="window" />
          <MenuButton k="help" />
        </div>

        <div style={styles.menuRight}>
          <p style={styles.menuIcon}>ðŸ”‹</p>
          <p style={styles.menuIcon}>ðŸ“¶</p>
          <p style={styles.timeText}>{time}</p>
        </div>
      </div>
    </div>
  );
};

