import React from "react";
import Window from "../os/Window";

export interface CreditsProps extends WindowAppProps {}

const RESUME_PDF_PATH = "/resume/Soham_Joshi_Resume.pdf";

const Credits: React.FC<CreditsProps> = (props) => {
  return (
    <Window
      top={48}
      left={48}
      width={1100}
      height={800}
      windowTitle="Resume"
      windowBarIcon="windowExplorerIcon"
      closeWindow={props.onClose}
      onInteract={props.onInteract}
      minimizeWindow={props.onMinimize}
      bottomLeftText={"© Copyright 2026 Soham Joshi"}
    >
      <div className="site-page" style={styles.wrap}>
        <div style={styles.topBar}>
          <div>
            <h2 style={{ margin: 0 }}>Resume</h2>
            <p style={{ margin: 0, opacity: 0.8 }}>Click “Download” if the PDF viewer is blocked.</p>
          </div>

          <a
            href={RESUME_PDF_PATH}
            download
            style={styles.button}
          >
            Download
          </a>
        </div>

        <div style={styles.viewer}>
          <iframe
            src={RESUME_PDF_PATH}
            title="Resume PDF"
            style={styles.iframe}
          />
        </div>
      </div>
    </Window>
  );
};

const styles: StyleSheetCSS = {
  wrap: {
    width: "100%",
    height: "100%",
    backgroundColor: "black",
    color: "white",
    padding: 24,
    boxSizing: "border-box",
    display: "flex",
    flexDirection: "column",
    gap: 16,
  },
  topBar: {
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
    gap: 16,
  },
  button: {
    display: "inline-flex",
    alignItems: "center",
    justifyContent: "center",
    padding: "10px 14px",
    border: "1px solid rgba(255,255,255,0.25)",
    backgroundColor: "rgba(255,255,255,0.08)",
    color: "white",
    textDecoration: "none",
    borderRadius: 6,
    cursor: "pointer",
    userSelect: "none",
    whiteSpace: "nowrap",
  },
  viewer: {
    flex: 1,
    width: "100%",
    border: "1px solid rgba(255,255,255,0.18)",
    backgroundColor: "white",
    overflow: "hidden",
    borderRadius: 6,
  },
  iframe: {
    width: "100%",
    height: "100%",
    border: "none",
    background: "white",
  },
};

export default Credits;
