import React, { useEffect, useMemo, useState } from 'react';
import { Icon } from '../general';

export interface DockProps {
  windows: DesktopWindows;
  toggleMinimize: (key: string) => void;
}

const Dock: React.FC<DockProps> = ({ windows, toggleMinimize }) => {
  const activeKeys = useMemo(() => Object.keys(windows), [windows]);

  return (
    <div style={styles.dockOuter}>
      <div style={styles.dockInner}>
        {activeKeys.map((key) => (
          <div
            key={key}
            style={styles.dockItem}
            onMouseDown={(e) => {
              e.preventDefault();
              toggleMinimize(key);
            }}
            title={windows[key].name}
          >
            <Icon icon={windows[key].icon} size={40} />
            {!windows[key].minimized && <div style={styles.dot} />}
          </div>
        ))}
      </div>
    </div>
  );
};

const styles: StyleSheetCSS = {
  dockOuter: {
    position: 'absolute',
    bottom: 10,
    left: 0,
    right: 0,
    zIndex: 100000,
    justifyContent: 'center',
    pointerEvents: 'none',
  },

  dockInner: {
    pointerEvents: 'auto',
    padding: '8px 14px',
    borderRadius: 16,
    gap: 14,

    // Snow Leopard-ish glass / shelf feel (subtle)
    backgroundColor: 'rgba(240,240,240,0.75)',
    border: '1px solid rgba(0,0,0,0.18)',
    boxShadow: '0 16px 30px rgba(0,0,0,0.25)',
  },

  dockItem: {
    position: 'relative',
    width: 52,
    height: 52,
    alignItems: 'center',
    justifyContent: 'center',
    cursor: 'pointer',
  },

  dot: {
    position: 'absolute',
    bottom: -4,
    width: 5,
    height: 5,
    borderRadius: 999,
    backgroundColor: 'rgba(0,0,0,0.65)',
  },
};

export default Dock;
