import React, { useEffect, useRef, useState } from 'react';
import { IconName } from '../../assets/icons';
import colors from '../../constants/colors';
import Colors from '../../constants/colors';
import Icon from '../general/Icon';
import DragIndicator from './DragIndicator';
import ResizeIndicator from './ResizeIndicator';

export interface WindowProps {
  closeWindow: () => void;
  minimizeWindow: () => void;
  onInteract: () => void;
  width: number;
  height: number;
  top: number;
  left: number;
  windowTitle?: string;
  bottomLeftText?: string;
  rainbow?: boolean;
  windowBarColor?: string;
  windowBarIcon?: IconName;
  onWidthChange?: (width: number) => void;
  onHeightChange?: (height: number) => void;
}

const Window: React.FC<WindowProps> = (props) => {
  const windowRef = useRef<any>(null);
  const dragRef = useRef<any>(null);
  const contentRef = useRef<any>(null);

  const dragProps = useRef<{
    dragStartX: any;
    dragStartY: any;
  }>();

  const resizeRef = useRef<any>(null);

  const [top, setTop] = useState(props.top);
  const [left, setLeft] = useState(props.left);

  const lastClickInside = useRef(false);

  const [width, setWidth] = useState(props.width);
  const [height, setHeight] = useState(props.height);

  const [contentWidth, setContentWidth] = useState(props.width);
  const [contentHeight, setContentHeight] = useState(props.height);

  const [windowActive, setWindowActive] = useState(true);

  const [isMaximized, setIsMaximized] = useState(false);
  const [preMaxSize, setPreMaxSize] = useState({
    width,
    height,
    top,
    left,
  });

  const [isDragging, setIsDragging] = useState(false);
  const [isResizing, setIsResizing] = useState(false);

  const startResize = (event: any) => {
    event.preventDefault();
    setIsResizing(true);
    window.addEventListener('mousemove', onResize, false);
    window.addEventListener('mouseup', stopResize, false);
  };

  const onResize = ({ clientX, clientY }: any) => {
    const curWidth = clientX - left;
    const curHeight = clientY - top;
    if (curWidth > 520) resizeRef.current.style.width = `${curWidth}px`;
    if (curHeight > 220) resizeRef.current.style.height = `${curHeight}px`;
    resizeRef.current.style.opacity = 1;
  };

  const stopResize = () => {
    setIsResizing(false);
    setWidth(resizeRef.current.style.width);
    setHeight(resizeRef.current.style.height);
    resizeRef.current.style.opacity = 0;
    window.removeEventListener('mousemove', onResize, false);
    window.removeEventListener('mouseup', stopResize, false);
  };

  const startDrag = (event: any) => {
    const { clientX, clientY } = event;
    setIsDragging(true);
    event.preventDefault();
    dragProps.current = {
      dragStartX: clientX,
      dragStartY: clientY,
    };
    window.addEventListener('mousemove', onDrag, false);
    window.addEventListener('mouseup', stopDrag, false);
  };

  const onDrag = ({ clientX, clientY }: any) => {
    const { x, y } = getXYFromDragProps(clientX, clientY);
    dragRef.current.style.transform = `translate(${x}px, ${y}px)`;
    dragRef.current.style.opacity = 1;
  };

  const stopDrag = ({ clientX, clientY }: any) => {
    setIsDragging(false);
    const { x, y } = getXYFromDragProps(clientX, clientY);
    setTop(y);
    setLeft(x);
    window.removeEventListener('mousemove', onDrag, false);
    window.removeEventListener('mouseup', stopDrag, false);
  };

  const getXYFromDragProps = (
    clientX: number,
    clientY: number
  ): { x: number; y: number } => {
    if (!dragProps.current) return { x: 0, y: 0 };
    const { dragStartX, dragStartY } = dragProps.current;

    const x = clientX - dragStartX + left;
    const y = clientY - dragStartY + top;

    return { x, y };
  };

  useEffect(() => {
    dragRef.current.style.transform = `translate(${left}px, ${top}px)`;
  });

  useEffect(() => {
    props.onWidthChange && props.onWidthChange(contentWidth);
  }, [props.onWidthChange, contentWidth]); // eslint-disable-line

  useEffect(() => {
    props.onHeightChange && props.onHeightChange(contentHeight);
  }, [props.onHeightChange, contentHeight]); // eslint-disable-line

  useEffect(() => {
    setContentWidth(contentRef.current.getBoundingClientRect().width);
  }, [width]);

  useEffect(() => {
    setContentHeight(contentRef.current.getBoundingClientRect().height);
  }, [height]);

  const maximize = () => {
    if (isMaximized) {
      setWidth(preMaxSize.width);
      setHeight(preMaxSize.height);
      setTop(preMaxSize.top);
      setLeft(preMaxSize.left);
      setIsMaximized(false);
    } else {
      setPreMaxSize({
        width,
        height,
        top,
        left,
      });
      setWidth(window.innerWidth);
      setHeight(window.innerHeight - 24);
      setTop(24);
      setLeft(0);
      setIsMaximized(true);
    }
  };

  const onCheckClick = () => {
    if (lastClickInside.current) setWindowActive(true);
    else setWindowActive(false);
    lastClickInside.current = false;
  };

  useEffect(() => {
    window.addEventListener('mousedown', onCheckClick, false);
    return () => {
      window.removeEventListener('mousedown', onCheckClick, false);
    };
  }, []);

  const onWindowInteract = () => {
    props.onInteract();
    setWindowActive(true);
    lastClickInside.current = true;
  };

  return (
    <div onMouseDown={onWindowInteract} style={styles.container}>
      <div
        style={Object.assign({}, styles.window, {
          width,
          height,
          top,
          left,
        })}
        ref={windowRef}
      >
        <div style={styles.windowBorderOuter}>
          <div style={styles.windowBorderInner}>
            {/* Drag region */}
            <div style={styles.dragHitbox} onMouseDown={startDrag} />

            {/* Snow Leopard Titlebar */}
            <div
              style={Object.assign(
                {},
                styles.topBarMac,
                !windowActive && styles.topBarMacInactive
              )}
            >
              {/* Traffic lights */}
              <div style={styles.trafficLights}>
                <div
                  style={Object.assign({}, styles.dot, styles.dotRed)}
                  onMouseDown={(e) => {
                    e.preventDefault();
                    e.stopPropagation();
                    props.closeWindow();
                  }}
                  title="Close"
                />
                <div
                  style={Object.assign({}, styles.dot, styles.dotYellow)}
                  onMouseDown={(e) => {
                    e.preventDefault();
                    e.stopPropagation();
                    props.minimizeWindow();
                  }}
                  title="Minimize"
                />
                <div
                  style={Object.assign({}, styles.dot, styles.dotGreen)}
                  onMouseDown={(e) => {
                    e.preventDefault();
                    e.stopPropagation();
                    maximize();
                  }}
                  title="Zoom"
                />
              </div>

              {/* Safari-style centered tab */}
              <div style={styles.safariTabWrap}>
                <div
                  style={Object.assign(
                    {},
                    styles.safariTab,
                    !windowActive && styles.safariTabInactive
                  )}
                >
                  {props.windowBarIcon ? (
                    <Icon
                      icon={props.windowBarIcon}
                      size={12}
                      style={styles.safariTabIcon}
                    />
                  ) : (
                    <div style={{ width: 12 }} />
                  )}

                  <span style={styles.safariTabTitle}>
                    {props.windowTitle || 'Untitled'}
                  </span>

                  {/* tiny tab close */}
                  <div
                    style={styles.safariTabClose}
                    onMouseDown={(e) => {
                      e.preventDefault();
                      e.stopPropagation();
                      props.closeWindow();
                    }}
                    title="Close Tab"
                  >
                    Ã—
                  </div>
                </div>
              </div>

              {/* subtle hairline highlight */}
              <div style={styles.topBarHighlight} />
            </div>

            <div style={styles.contentOuter}>
              <div style={styles.contentInner}>
                <div style={styles.content} ref={contentRef}>
                  {props.children}
                </div>
              </div>
            </div>

            <div onMouseDown={startResize} style={styles.resizeHitbox} />

            <div style={styles.bottomBar}>
              <div
                style={Object.assign({}, styles.insetBorder, {
                  flex: 5 / 7,
                  alignItems: 'center',
                })}
              >
                <p style={styles.bottomLeftText}>
                  {props.bottomLeftText}
                </p>
              </div>

              <div style={Object.assign({}, styles.insetBorder, styles.bottomSpacer)} />
              <div style={Object.assign({}, styles.insetBorder, styles.bottomSpacer)} />

              <div
                style={Object.assign(
                  {},
                  styles.insetBorder,
                  styles.bottomResizeContainer
                )}
              >
                <div style={{ alignItems: 'flex-end' }}>
                  <Icon size={12} icon="windowResize" />
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Resize indicator */}
      <div
        style={
          !isResizing
            ? { zIndex: -10000, pointerEvents: 'none' }
            : { zIndex: 1000, cursor: 'nwse-resize', mixBlendMode: 'difference' }
        }
      >
        <ResizeIndicator
          top={top}
          left={left}
          width={width}
          height={height}
          resizeRef={resizeRef}
        />
      </div>

      {/* Drag indicator */}
      <div
        style={
          !isDragging
            ? { zIndex: -10000, pointerEvents: 'none' }
            : { zIndex: 1000, cursor: 'move', mixBlendMode: 'difference' }
        }
      >
        <DragIndicator width={width} height={height} dragRef={dragRef} />
      </div>
    </div>
  );
};

